$(document).ready(function(){let t=document.querySelector("#searchForm"),c=document.querySelector("#searchForm .search-input"),e=document.querySelector("#searchButton");e.addEventListener("click",e=>{t.classList.contains("active")||e.preventDefault(),t.classList.toggle("active"),c.focus()}),window.onscroll=()=>{t.classList.remove("active")},document.addEventListener("click",e=>{t.contains(e.target)||t.classList.remove("active")})});